#include "primitives/block/block.hpp"

#include <gtest/gtest.h>
#include "testutil/cbor.hpp"

/// Block header CBOR encoding and decoding
TEST(BlockTest, BlockHeaderCbor) {
  auto bls1 = "010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101"_blob96;
  auto bls2 = "020101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101010101"_blob96;
  fc::primitives::block::BlockHeader block{
    fc::primitives::address::Address::makeFromId(1),
    boost::none,
    {
      fc::common::Buffer("F00D"_unhex),
      bls1,
      {},
    },
    {"010001020002"_cid},
    fc::primitives::BigInt(3),
    4,
    "010001020005"_cid,
    "010001020006"_cid,
    "010001020007"_cid,
    // TODO(turuslan): FIL-72 signature
    "01CAFE"_unhex,
    8,
    boost::none,
  };
  expectEncodeAndReencode(block, ""_unhex);
  block.ticket = fc::primitives::ticket::Ticket{bls2};
  // TODO(turuslan): FIL-72 signature
  block.block_sig = "01DEAD"_unhex;
  expectEncodeAndReencode(block, ""_unhex);
}
